HelpDataItem.example:
function getItemByName(name)
	invList = GetInventory();
	for item in invList.list do
		if item.Name == name then
			return item.objectId;
			end;
	end;
end;

TargetNpc("Valkon", 31267);
GetWarehouseManager():Clear();
GetWarehouseManager():AddItem(getItemByName("Adena"),4);
Talk();
ClickAndWait("npc_268458429_Chat 1", "Private Warehouse");
while(not GetWarehouseManager():GetStatus()) do
ClickAndWait("npc_268458429_DepositP", "Deposit an item - Private Warehouse");
Sleep(100);
end;
GetWarehouseManager():DepositItems();

